import React, { useEffect, useState } from 'react';
import '../../css/exam.css';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const Exam = () => {
    const location = useLocation();
    const email = location.state?.email;
    const [user, setUser] = useState([]);
    const [userRole, setUserRole] = useState('');

    async function getUserData() {
        try {
            const res = await axios.get(`http://localhost:5000/api/users/${email}`);

            if (res.data.success) {
                setUser(res.data.user);
                if (res.data.user.isAdmin) {
                    setUserRole("Admin");
                } else {
                    setUserRole("User");
                }

            } else {

                console.error("Error fecting data");
            }
        } catch (error) {

        }
    };

    useEffect(() => {
        getUserData();
        //getAllExams();
    }, []);

    return (
        <div>

            <div className='div-box'>
                <div className='left-side-nav-bar'>

                    <div className='nav-bar'>
                        <div className='home-btn'>
                            <label>Home</label>
                        </div>

                        <div className='report-btn'>
                            <label>Reports</label>
                        </div>

                        {user.isAdmin && (
                            <div className='exam-btn'>
                                <label>Exam</label>
                            </div>
                        )}

                        <div className='logout-btn'>
                            <label>Logout</label>
                        </div>
                    </div>

                </div>

                <div className='top-menu'>
                    <label className='title'>Quiz Application</label>
                    <div className='details'>
                        <label className='name'>Name: {user?.username}</label><br />
                        <label className='role'>Role: {userRole}</label>
                    </div>
                </div>

                <div className='main-exam-div'>
                    <table >
                        <tr >
                            <th>Exam Name</th>
                            <th>Duration</th>
                            <th>Category</th>
                            <th>Total Marks</th>
                            <th>Passing Marks</th>
                            <th>Action</th>
                        </tr>
                        {[...Array(10)].map((_, index) => (
                            <tr key={index}>
                                <td>GK</td>
                                <td>60</td>
                                <td>Java</td>
                                <td>10</td>
                                <td>1</td>
                                <td>Action</td>
                            </tr>
                        ))}
                    </table>

                </div>

            </div>
        </div>
    );
};

export default Exam;
