import React, { useEffect, useState } from 'react';
import '../css/startExam.css';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const StartExam = () => {
    const [userRole, setUserRole] = useState('');
    const [exam, setExam] = useState([]);
    const [examDetail, setExamDetail] = useState([]);   
    const [score, setScore] = useState(0);
    const [answers, setAnswers] = useState({});
    const [count, setCount] = useState(0);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [showResult, setShowResult] = useState(false);

    const navigate = useNavigate();
    const user = JSON.parse(sessionStorage.getItem("user"));
    const location = useLocation();
    const examId = location.state?.examId;

    async function getExamQuesion() {
        try {
            const res = await axios.get(`http://localhost:5000/api/users/exam/${examId}`);
            if (res.data.success) {
                setExam(res.data.examDetail);
            }
        } catch (error) {
            console.error("Error fetching Exam question: ", error);
        }
    };

    async function getExamDetails() {
        try {
            const res = await axios.get(`http://localhost:5000/api/users/exam-detail/${examId}`);
            if (res.data.success) {
                setExamDetail(res.data.examDetails);
                setCount(Number(res.data.examDetails.duration) || 0);
            } else {
                console.log("Error fetching exam details");
            }
        } catch (error) {
            console.error("Error fetching exam details: ", error);
        }
    };

    function logout() {
        sessionStorage.removeItem("user");
        sessionStorage.removeItem("userEmail");
        localStorage.removeItem("token");
        navigate('/login');
    }

    const handleOptionChange = (questionIndex, selectedOption) => {
        setAnswers(prev => ({
            ...prev,
            [questionIndex]: selectedOption
        }));

        if (examDetail[questionIndex]?.correctOption === selectedOption) {
            setScore(prevScore => prevScore + 1);
        }
    };

    const handleNext = () => {
        if (currentQuestion < exam.length - 1) {
            setCurrentQuestion((prev) => prev + 1);
        }
    };

    const handlePrevious = () => {
        if (currentQuestion > 0) {
            setCurrentQuestion((prev) => prev - 1);
        }
    };

    const handleSubmit = () => {
        let calculatedScore = 0;
        exam.forEach((question, index) => {
            if (answers[index] === examDetail[index]?.correctOption) {
                calculatedScore += 1;
            }
        });
        setScore(calculatedScore);
        setShowResult(true);
    };

    useEffect(() => {
        getExamQuesion();
        getExamDetails();
    }, []);

    useEffect(() => {
        if (count > 0) {
            const interval = setInterval(() => {
                setCount(prevCount => {
                    if (prevCount === 0) {
                        clearInterval(interval);
                        alert("Time up!");
                        setShowResult(true);
                        return prevCount;
                    }
                    return prevCount - 1;
                });
            }, 1000);

            return () => clearInterval(interval);
        }
    }, [count]);

    if (showResult) {
        return (
            <div className="result-container">
                <h2 className="result-title">RESULT</h2>
                <hr className="result-divider" />
                <div className="result-details">
                    <p>Total Marks : {examDetail.length}</p>
                    <p>Obtained Marks : {score}</p>
                    <p>Wrong Answers : {exam.length - score}</p>
                    <p>Passing Marks : {Math.ceil(exam.length * 0.6)}</p>
                    <p>VERDICT : {score >= Math.ceil(exam.length * 0.6) ? "Pass" : "Fail"}</p>
                </div>
                <div className="result-actions">
                    <button className="btn btn-retake" onClick={() => navigate("/")}>
                        Retake Exam
                    </button>
                    <button className="btn btn-review" onClick={() => console.log(answers)}>
                        Review Answers
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className='div-box'>
            <div className='left-side-nav-bar'>
                <div className='nav-bar'>
                    <div className='home-btn'><label>Home</label></div>
                    <div className='report-btn' onClick={() => navigate('/reports')}><label>Reports</label></div>
                    {user?.isAdmin && (
                        <div className='exam-btn' onClick={() => navigate('/exam')}>
                            <label>Exam</label>
                        </div>
                    )}
                    <div className='logout-btn' onClick={logout}><label>Logout</label></div>
                </div>
            </div>

            <div className='top-menu'>
                <label className='title'>Quiz Application</label>
                <div className='details'>
                    <label className='name'>Name: {user?.username}</label><br />
                    <label className='role'>Role: {userRole}</label>
                </div>
            </div>

            <div className="quiz-container">
                <h1>Quiz Application</h1>
                <div className="question">
                    <span className="question-number">{currentQuestion + 1} :</span>
                    <span className="question-text">{exam[currentQuestion]?.name}</span>
                    <div className="timer">{count}</div>
                </div>
                <div className="options">
                    {exam[currentQuestion]?.options &&
                        Object.keys(exam[currentQuestion].options).map((key) => {
                            const value = exam[currentQuestion].options[key];
                            return (
                                <label key={key} className={`option ${answers[currentQuestion] === key ? "selected" : ""}`}>
                                    <input
                                        type="radio"
                                        name={`option-${currentQuestion}`}
                                        checked={answers[currentQuestion] === key}
                                        onChange={() => handleOptionChange(currentQuestion, key)}
                                    />
                                    {`${key}: ${value}`}
                                </label>
                            );
                        })}
                </div>
                <div className="buttons">
                    <button
                        className="previous-button"
                        onClick={handlePrevious}
                        disabled={currentQuestion === 0}
                    >
                        Previous
                    </button>
                    {currentQuestion === exam.length - 1 ? (
                        <button className="submit-button" onClick={handleSubmit}>
                            Submit
                        </button>
                    ) : (
                        <button className="next-button" onClick={handleNext}>
                            Next
                        </button>
                    )}
                </div>
            </div>
        </div>
    );
};

export default StartExam;
