import { useState } from 'react'
import axios from 'axios'
import {useNavigate} from 'react-router-dom'
import '../css/login.css';

function Login(){

    const [username, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/api/users/signup", {
                username,
                email,
                password,
            });
    
            if (response.data.success) {
                alert("Signup successful! Please log in.");
                navigate('/home');  // Redirect to login page after successful signup
            } else {
                alert(response.data.message || "Signup failed. Please try again.");
            }
        } catch (error) {
            console.error("Signup error:", error);
            alert(error.response?.data?.message || "Something went wrong. Please try again.");
        }
    };

    
    return(
        <div className='main-body'>
            <div className='form-div'>
            <hr/>
            <form onSubmit={handleSignup}>
            <h1>Login</h1>
                <label>Name</label><br/>
                <input className='name-in' type='text' placeholder='Name' value={username} onChange={(e)=>setName(e.target.value)} required/><br/>
                <label>Email</label><br/>
                <input className='email-in' type='text' placeholder='Email' value={email} onChange={(e)=>setEmail(e.target.value)}required/><br/>
                <label>Password</label><br/>
                <input className='password-in' type='password' placeholder='Password' value={password} onChange={(e)=>setPassword(e.target.value)}required/><br/>
                <button className='btn-reg' type='submit'>Register</button>
            </form>
            </div>
        </div>
    );
};

export default Login;
