import React, {useEffect, useState} from 'react';
import '../css/home.css';
import { useLocation } from 'react-router-dom';

const Home = () => {
    const location = useLocation();
    const email = location.state?.email;
    const [user, setUser] = useState([]);

    async function getUserData(){
        try {
            const res = await axios.get(`http://localhost:5000/api/users/${email}`);
            if(!res.success){
                console.error("Error fecting data");
            } else {
                setUser(res.data.user);
            }
        } catch (error) {
            
        }
    };
    
    useEffect(()=>{
        getUserData();
        //getAllExams();
    },[]);

    return (
        <div>

            <div className='div-box'>
                <div className='left-side-nav-bar'>

                    <div className='nav-bar'>
                        <div className='home-btn'>
                            <label>Home</label>
                        </div>

                        <div className='report-btn'>
                            <label>Reports</label>
                        </div>

                        <div className='logout-btn'>
                            <label>Logout</label>
                        </div>
                    </div>

                </div>

                <div className='top-menu'>
                    <label className='title'>Quiz Application</label>
                    <label className='name'>Boopathyraj: {user?.username}</label>
                    <label className='role'>Role: {user?.email}</label>
                </div>
            </div>
        </div>
    );
};

export default Home;
