import React, { useEffect, useState } from "react";
import "../css/startExam.css";
import axios from "axios";
import { useLocation, useNavigate } from "react-router-dom";

const StartExam = () => {
    const [userRole, setUserRole] = useState("");
    const [exam, setExam] = useState([]);
    const [count, setCount] = useState(10);
    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [answers, setAnswers] = useState([]);
    const navigate = useNavigate();
    const user = JSON.parse(sessionStorage.getItem("user"));
    const location = useLocation();
    const examId = location.state?.examId;

    useEffect(() => {
        async function fetchExam() {
            try {
                const res = await axios.get(`http://localhost:5000/api/users/exam/${examId}`);
                if (res.data.success) {
                    setExam(res.data.examDetail);
                    setAnswers(Array(res.data.examDetail.length).fill(null));
                }
            } catch (error) {
                console.error("Error fetching exam question:", error);
            }
        }
        fetchExam();

        setUserRole(user?.isAdmin ? "Admin" : "User");

        const interval = setInterval(() => {
            setCount((prevCount) => {
                if (prevCount === 0) {
                    clearInterval(interval);
                    return prevCount;
                }
                return prevCount - 1;
            });
        }, 1000);

        return () => clearInterval(interval);
    }, [examId, user]);

    const handleOptionChange = (index) => {
        const updatedAnswers = [...answers];
        updatedAnswers[currentQuestion] = index;
        setAnswers(updatedAnswers);
    };

    const handleNext = () => {
        if (currentQuestion < exam.length - 1) {
            setCurrentQuestion((prev) => prev + 1);
        }
    };

    const handlePrevious = () => {
        if (currentQuestion > 0) {
            setCurrentQuestion((prev) => prev - 1);
        }
    };

    const handleSubmit = () => {
        console.log("User Answers:", answers);
        alert("Quiz Submitted! Check the console for user responses.");
    };

    const logout = () => {
        sessionStorage.removeItem("user");
        sessionStorage.removeItem("userEmail");
        localStorage.removeItem("token");
        navigate("/login");
    };

    return (
        <div className="div-box">
            <div className="left-side-nav-bar">
                <div className="nav-bar">
                    <div className="home-btn">
                        <label>Home</label>
                    </div>
                    <div className="report-btn" onClick={() => navigate("/reports")}>
                        <label>Reports</label>
                    </div>
                    {user?.isAdmin && (
                        <div className="exam-btn" onClick={() => navigate("/exam")}>
                            <label>Exam</label>
                        </div>
                    )}
                    <div className="logout-btn" onClick={logout}>
                        <label>Logout</label>
                    </div>
                </div>
            </div>
            <div className="top-menu">
                <label className="title">Quiz Application</label>
                <div className="details">
                    <label className="name">Name: {user?.username}</label>
                    <br />
                    <label className="role">Role: {userRole}</label>
                </div>
            </div>
            <div className="quiz-container">
                {exam.length > 0 && (
                    <>
                        <div className="question">
                            <span className="question-number">{currentQuestion + 1} :</span>
                            <span className="question-text">{exam[currentQuestion].name}</span>
                            <div className="timer">{count}</div>
                        </div>
                        <div className="options">
                            {exam[currentQuestion]?.options?.map((option, index) => (
                                <label
                                    key={index}
                                    className={`option ${
                                        answers[currentQuestion] === index ? "selected" : ""
                                    }`}
                                >
                                    <input
                                        type="radio"
                                        name="option"
                                        checked={answers[currentQuestion] === index}
                                        onChange={() => handleOptionChange(index)}
                                    />
                                    {option}
                                </label>
                            ))}
                        </div>
                        <div className="buttons">
                            <button
                                className="previous-button"
                                onClick={handlePrevious}
                                disabled={currentQuestion === 0}
                            >
                                Previous
                            </button>
                            {currentQuestion === exam.length - 1 ? (
                                <button className="submit-button" onClick={handleSubmit}>
                                    Submit
                                </button>
                            ) : (
                                <button className="next-button" onClick={handleNext}>
                                    Next
                                </button>
                            )}
                        </div>
                    </>
                )}
            </div>
        </div>
    );
};

export default StartExam;
