import React, { useEffect, useState } from 'react';
import '../css/home.css';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const Home = () => {
    const location = useLocation();
    const email1 = location.state?.email;
    const [user, setUser] = useState([]);
    const [userRole, setUserRole] = useState('');
    const navigate = useNavigate();

    const email = sessionStorage.getItem("userEmail");

    async function getUserData() {
        try {
            const res = await axios.get(`http://localhost:5000/api/users/${email}`);

            if (res.data.success) {
                setUser(res.data.user);
                sessionStorage.setItem("user", JSON.stringify(res.data.user));
                if(res.data.user.isAdmin){
                    setUserRole("Admin");
                } else{
                    setUserRole("User");
                }
                
            } else {

                console.error("Error fecting data");
            }
        } catch (error) {

        }
    };

    function logout(){
        sessionStorage.removeItem("user");
    }
    useEffect(() => {
        getUserData();
        //getAllExams();
    }, []);

    return (
        <div>

            <div className='div-box'>
                <div className='left-side-nav-bar'>

                    <div className='nav-bar'>
                        <div className='home-btn'>
                            <label>Home</label>
                        </div>

                        <div className='report-btn' onClick={()=>navigate('/reports')}>
                            <label>Reports</label>
                        </div>
                        
                        {user.isAdmin &&(
                            <div className='exam-btn' onClick={()=>navigate('/exam')}>
                                <label>Exam</label>
                            </div>
                        )}

                        <div className='logout-btn'>
                            <label>Logout</label>
                        </div>
                    </div>

                </div>

                <div className='top-menu'>
                    <label className='title'>Quiz Application</label>
                    <div className='details'>
                        <label className='name'>Name: {user?.username}</label><br />
                        <label className='role'>Role: {userRole}</label>
                    </div>
                </div>

                <div className='main-exam-div'>
                    {/* Grid Container */}
                    <div className='grid-div-exam'>
                        {[...Array(10)].map((_, index) => (
                            <div className='item'
                             key={index}>
                                <label id='exam-title'>Gkfhdfht</label><br/>
                                <label>Category: </label><br/>
                                <label>Total Marks: </label><br/>
                                <label>Passing Marks: </label><br/>
                                <label>Duration Marks: </label><br/>
                                <button>Start Exam</button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Home;
