import React, { useEffect, useState } from 'react';
import '../css/startExam.css';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const StartExam = () => {
    const [userRole, setUserRole] = useState('');
    const [exam, setExam] = useState([]);
    const [examDetail, setExamDetail] = useState([]);   
    const [count, setCount] = useState(0);
    const navigate = useNavigate();
    const user = JSON.parse(sessionStorage.getItem("user"));
    const location = useLocation();
    const examId = location.state?.examId;

    async function getExamQuesion() {
        try {
            const res = await axios.get(`http://localhost:5000/api/users/exam/${examId}`);
            if (res.data.success) {
                setExam(res.data.examDetail);
            }
        } catch (error) {
            console.error("Error fetching Exam question: ", error);
        }
    };

    async function getExamDetails() {
        try {
            const res = await axios.get(`http://localhost:5000/api/users/exam-detail/${examId}`);
            if (res.data.success) {
                console.log("exam",res.data.examDetails);
                setExamDetail(res.data.examDetails);
            } else{
                console.log("error");
            }
        } catch (error) {
            console.error("Error fetching Exam question: ", error);
        }
    };

    function logout() {
        sessionStorage.removeItem("user");
        sessionStorage.removeItem("userEmail");
        localStorage.removeItem("token");
        navigate('/login');
    }

    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [answers, setAnswers] = useState([]);

    const handleOptionChange = (questionIndex, selectedOption) => {
        setAnswers(prev => ({
            ...prev,
            [questionIndex]: selectedOption
        }));
    
        // Check if the selected option matches the correct answer
        if (examDetail[questionIndex]?.correctOption === selectedOption) {
            setScore(prevScore => prevScore + 1); // Increase score by 1
        }
    };

    const handleNext = () => {
        if (currentQuestion < exam.length - 1) {
            setCurrentQuestion((prev) => prev + 1);
        }
    };

    const handlePrevious = () => {
        if (currentQuestion > 0) {
            setCurrentQuestion((prev) => prev - 1);
        }
    };

    const handleSubmit = () => {
        console.log("User Answers:", answers);
        alert("Quiz Submitted! Check the console for user responses.");
    };

    useEffect(() => {
        getExamQuesion();
        getExamDetails(); // This updates examDetail asynchronously
    }, []);
    
    // Watch `examDetail` and update `count` once it's available
    useEffect(() => {
            setCount(Number(examDetail.duration) || 0);
    }, [examDetail]);
    
    useEffect(() => {
        if (count > 0) {
            const interval = setInterval(() => {
                setCount(prevCount => {
                    if (prevCount === 0) {
                        clearInterval(interval);
                        alert("Time up!");
                        return prevCount;
                    }
                    return prevCount - 1;
                });
            }, 1000);
    
            return () => clearInterval(interval);
        }
    }, [count]); // Start interval only when count is set


    return (
        <div>
            <div className='div-box'>
                <div className='left-side-nav-bar'>
                    <div className='nav-bar'>
                        <div className='home-btn'><label>Home</label></div>
                        <div className='report-btn' onClick={() => navigate('/reports')}><label>Reports</label></div>
                        {user?.isAdmin && (
                            <div className='exam-btn' onClick={() => navigate('/exam')}>
                                <label>Exam</label>
                            </div>
                        )}
                        <div className='logout-btn' onClick={logout}><label>Logout</label></div>
                    </div>
                </div>

                <div className='top-menu'>
                    <label className='title'>Quiz Application</label>
                    <div className='details'>
                        <label className='name'>Name: {user?.username}</label><br />
                        <label className='role'>Role: {userRole}</label>
                    </div>
                </div>

                <div className="quiz-container">
                    <h1 className="header">Employee</h1>
                    <div className="question">
                        <span className="question-number">{currentQuestion + 1} :</span>
                        <span className="question-text">{exam[currentQuestion]?.name}</span>
                        <div className="timer">{count}</div>
                    </div>
                    <div className="options">
                        {exam[currentQuestion]?.options &&
                            Object.keys(exam[currentQuestion].options).map((key) => {
                                const value = exam[currentQuestion].options[key];
                                return (
                                    <label key={key} className={`option ${answers[currentQuestion] === key ? "selected" : ""}`}>
                                        <input
                                            type="radio"
                                            name="option"
                                            checked={answers[currentQuestion] === key}
                                            onChange={() => handleOptionChange(key)}
                                        />
                                        {`${key}: ${value}`}
                                    </label>
                                );
                            })}
                    </div>
                    <div className="buttons">
                        <button
                            className="previous-button"
                            onClick={handlePrevious}
                            disabled={currentQuestion === 0}
                        >
                            Previous
                        </button>
                        {currentQuestion === exam.length - 1 ? (
                            <button className="submit-button" onClick={handleSubmit}>
                                Submit
                            </button>
                        ) : (
                            <button className="next-button" onClick={handleNext}>
                                Next
                            </button>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StartExam;
