import { useState } from "react"
import axios from 'axios'
import { useNavigate } from "react-router-dom";

function Login() {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    async function login(e){
        e.preventDefault();

        try {
            const res = await axios.post("http://localhost:5000/api/users/login",{
                username,
                password
            });

            if(res.data.success){
                alert("Login successful");
                navigate('/home');
            }else{
                alert("Error", res.data.message);
            }
        } catch (error) {
            console.error("Login error: ", error);
            alert("Something went wrong");
        }

    };

    return (
        <div className="main-div">
            <form onSubmit={login} className="form-div">
                <input className="name-in" type="text" value={username} onChange={(e) => setUsername(e.target.value)} required></input>
                <input className="password-in" type="password" value={password} onChange={(e) => setPassword(e.target.value)} required></input>
                <button className="btn-login" type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
