import React, { useEffect, useState } from 'react';
import '../css/startExam.css';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const questions = [
    {
        id: 1,
        question: "What is React?",
        options: ["A library", "A framework", "A database", "None of the above"],
    },
    {
        id: 2,
        question: "What is JSX?",
        options: [
            "JavaScript XML",
            "Java Syntax",
            "JavaScript Extension",
            "None of the above",
        ],
    },
    {
        id: 3,
        question: "What is a component?",
        options: [
            "A function",
            "A class",
            "Reusable UI piece",
            "All of the above",
        ],
    },
];

const StartExam = () => {
    const [userRole, setUserRole] = useState('');
    const [exam, setExam] = useState([]);
    const [count, setCount] = useState(10);
    const navigate = useNavigate();
    const user = JSON.parse(sessionStorage.getItem("user"));
    const examId = location.state?.examId;

    async function getExam() {
        try {
            const res = await axios.get(`http://localhost:5000/api/users/exam/${e}`);

            if (res.data.success) {
                setExam(res.data.allExam);

            }
        } catch (error) {
            console.error("Errro fetching Exam:", error);
        }
    }

    function logout() {
        sessionStorage.removeItem("user");
        sessionStorage.removeItem("userEmail");
        localStorage.removeItem("token");
        navigate('/login');
    }

    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [answers, setAnswers] = useState(Array(questions.length).fill(null));

    const handleOptionChange = (index) => {
        const updatedAnswers = [...answers];
        updatedAnswers[currentQuestion] = index;
        setAnswers(updatedAnswers);
    };

    const handleNext = () => {
        if (currentQuestion < questions.length - 1) {
            setCurrentQuestion((prev) => prev + 1);
        }
    };

    const handlePrevious = () => {
        if (currentQuestion > 0) {
            setCurrentQuestion((prev) => prev - 1);
        }
    };

    const handleSubmit = () => {
        console.log("User Answers:", answers);
        alert("Quiz Submitted! Check the console for user responses.");
    };

    useEffect(() => {

        const interval = setInterval(() => {
            setCount(prevCount => {
                if (prevCount == 0) {
                    clearInterval(interval); // Stop at 20
                    return prevCount;
                }
                return prevCount - 1; // Increment counter
            });
        }, 1000); // Update every second

        return () => clearInterval(interval);
        getExam();
        //getAllExams();
    }, []);

    return (
        <div>

            <div className='div-box'>
                <div className='left-side-nav-bar'>

                    <div className='nav-bar'>
                        <div className='home-btn'>
                            <label>Home</label>
                        </div>

                        <div className='report-btn' onClick={() => navigate('/reports')}>
                            <label>Reports</label>
                        </div>

                        {user?.isAdmin && (
                            <div className='exam-btn' onClick={() => navigate('/exam')}>
                                <label>Exam</label>
                            </div>
                        )}

                        <div className='logout-btn' onClick={logout}>
                            <label>Logout</label>
                        </div>
                    </div>

                </div>

                <div className='top-menu'>
                    <label className='title'>Quiz Application</label>
                    <div className='details'>
                        <label className='name'>Name: {user?.username}</label><br />
                        <label className='role'>Role: {userRole}</label>
                    </div>
                </div>
                <div className="quiz-container">
                    <h1 className="header">Employee</h1>
                    <div className="question">
                        <span className="question-number">{currentQuestion + 1} :</span>
                        <span className="question-text">
                            {questions[currentQuestion].question}
                        </span>
                        <div className="timer">{}</div>
                    </div>
                    <div className="options">
                        {questions[currentQuestion].options.map((option, index) => (
                            <label key={index} className={`option ${answers[currentQuestion] === index ? "selected" : ""}`}>
                                <input
                                    type="radio"
                                    name="option"
                                    checked={answers[currentQuestion] === index}
                                    onChange={() => handleOptionChange(index)}
                                />
                                {option}
                            </label>
                        ))}
                    </div>
                    <div className="buttons">
                        <button
                            className="previous-button"
                            onClick={handlePrevious}
                            disabled={currentQuestion === 0}
                        >
                            Previous
                        </button>
                        {currentQuestion === questions.length - 1 ? (
                            <button className="submit-button" onClick={handleSubmit}>
                                Submit
                            </button>
                        ) : (
                            <button className="next-button" onClick={handleNext}>
                                Next
                            </button>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StartExam;
